mapper.select:
SELECT COUNT(0) 
FROM cschedule 
WHERE cns_no=#{cns_no} AND sch_ymd=#{sch_ymd} AND sch_hr=#{sch_hr}

SELECT aply_no, stud_no, dscsn_ymd, dscsn_hr, GET_CD_NM('CS', a.dscsn_stat) AS dscsn_stat, aply_no_old, dscsn_cn, dscsn_nmtm, a.sch_no 
FROM caply a JOIN cschedule s ON a.sch_no = s.sch_no  
WHERE s.cns_no=#{cns_no}

SELECT aply_no, aply_no_old, GET_CNS_NM(cns_no) AS cns_nm, GET_CNS_FIELD_NM(cns_no) AS cns_field, cns_no, GET_STUD_NM(stud_no) AS stud_nm, stud_no, dscsn_ymd, dscsn_hr, dscsn_stat, dscsn_cn, sch_no FROM csche_ply
aply_no IS NOT NULL
AND cns_no = #{cns_no}
AND stud_no = #{stud_no}
ORDER BY STR_TO_DATE(dscsn_ymd, '%Y%m%d') DESC, STR_TO_DATE(dscsn_hr, '%H') DESC LIMIT #{page}, 10

SELECT COUNT(0) FROM csche_ply 

	aply_no IS NOT NULL 

		AND cns_no = #{cns_no}


		AND stud_no = #{stud_no}
