mapper.insert:

INSERT INTO online
(btitle, bcontent, user_no) 
VALUES (#{btitle}, #{bcontent}, #{uno});

mapper.select:
select  bno, btitle, bcontent, bdate, secret, commentYN, bdel, uname 
FROM onlineview 
ORDER BY bno DESC LIMIT #{page},#{size}

mapper.update:
		update online 
		set bdel = '0'
 		where bno = #{bno}
